sys_variable_value.value:
(function execute(inputs, outputs) {
  	var instance = gs.getProperty('instance_name');
	var url = "https://" + instance + ".service-now.com/now/optimiz-workspace/home/params/sysparm/%5Enumber%3D" + inputs.alert_num + "%5EORDERBYDESCnumber%5EORDERBYorder/list/all";
	outputs.contextual_workspace_url = url;
})(inputs, outputs);

