sys_variable_value.value:
(function execute(inputs, outputs) {
  try {
    var add_info = JSON.parse(inputs.additional_info);
    var itom_tags = add_info.itom_tags;
    var keys = Object.keys(itom_tags);
    var outputString = "";
    for(var i = 0; i < keys.length; i++) {
      outputString += keys[i] + ": " + itom_tags[keys[i]];
      if (i != keys.length - 1) {
          outputString += ", ";
      }
    }
    if (outputString.length == 0) {
      outputString += "No normalized tags";
    }
    outputs.comma_separated_itom_tags = outputString;
  } catch (e) {
  	outputs.comma_separated_itom_tags = "No normalized tags";
  }

})(inputs, outputs);

