sys_variable_value.value:
(function execute(inputs, outputs) {
	var gr = new GlideRecord("sys_journal_field");
  	gr.addQuery("name", "em_alert");
	gr.addQuery("element_id", inputs.alert_sys_id);
	gr.orderByDesc("sys_created_by");
  	gr.query();
  	var foundClusteringMethod = false;
  	var clusteringMethod = "";
  	while (gr.next() && foundClusteringMethod == false) {
      	var work_note_value = gr.getDisplayValue("value");
      	//var regex = new RegExp("(<a.*>\[Tag Based\]).*(?=<\/a>)", "gi");
      	var method = work_note_value.match(/(<a.*>\[Tag Based\]).*(?=<\/a>)/gi);
      	if (method && method[0]) {
          	method[0] = method[0].substring(method[0].indexOf("]")); 
        	foundClusteringMethod = true;
          	clusteringMethod = method[0];
        }
    }
  	outputs.tag_based_clustering_method = clusteringMethod;

})(inputs, outputs);

