sys_variable_value.value:
(function execute(inputs, outputs) {
  	var app_services = "";
	while (inputs.app_service_records.next()) {
    	app_services += inputs.app_service_records.getDisplayValue("service_id") + ", ";
    }
  	if (app_services.charAt(app_services.length - 2) == ",") {
    	app_services = app_services.substring(0, app_services.length - 2);
    }
  	outputs.comma_seperated_application_services = app_services;
})(inputs, outputs);

