sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function handler({api, event, helpers, imports}) {
    let url = "/now/nav/ui/classic/params/target/u_alert_tag_normalization.do%3Fsys_id%3D";
    url += event.payload.data.output.data.GlideRecord_Mutation.insert_u_alert_tag_normalization.sys_id.value;
    api.emit("NEW_TAB_URL_UPDATED", {url: url});
}
